mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			     SELECT b.num, b.userId, subject
			           ,b.created
					   ,hitCount
					   ,NVL(replyCount, 0) replyCount
			        FROM bbs b
					LEFT OUTER JOIN (
					    SELECT num, COUNT(*) replyCount FROM bbsReply
					    WHERE answer = 0
					    GROUP BY num
					) c ON b.num = c.num
                    
                         
			                  
		                 
	                
	                ORDER BY num DESC
	
	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}
